import React from 'react';
import videojs from 'video.js';
import 'video.js/dist/video-js.css';

class App extends React.Component {
componentDidMount() {
  //Assuming the video player is assigned to a ref named 'videoNode'
  this.player = videojs(this.videoNode, this.props, function onPlayerReady() {
    console.log('Video Player is ready', this);
  });

  // Assuming 'hlsSource' is the URL of your HLS stream
    const hlsSource = 'https://devstreaming-cdn.apple.com/videos/streaming/examples/img_bipbop_adv_example_fmp4/master.m3u8';  this.player.src({
    src: hlsSource,
    type: 'application/x-mpegURL'
  });
}

  handleClick = () => {
    // This is where we'll make a request to the backend
    console.log('Button clicked!');
  }

  render() {
    return (
      <div style={{ fontFamily: 'Arial, sans-serif', padding: '10px', backgroundColor: '#333333' }}>
        <h1 style={{ textAlign: 'center', color: '#f0f0f0' }}>HLS Highlight</h1>
        <div id="video-player" style={{ display: 'flex', justifyContent: 'center', marginBottom: '20px' }}>
          <video ref={node => this.videoNode = node} className='video-js' controls preload='auto' width='1280' height='536' data-setup='{}'/>
        </div>
        <div id="timeline" style={{  marginBottom: '20px' }}>
        <body style={{ color: 'fff'}}>Timeline</body>
        </div>

        <button onClick={this.handleClick} style={{ padding: '10px 20px', fontSize: '16px', backgroundColor: '#007BFF', color: 'white', borderRadius: '4px', cursor: 'pointer' }}>
          Get Video Data
        </button>
      </div>
    );
  }
}

export default App;
