import 'bootstrap/dist/css/bootstrap.min.css';
import React from 'react';
import { Container, Dropdown, Navbar } from 'react-bootstrap';
import { Link, Route, BrowserRouter as Router, Switch } from 'react-router-dom';
import 'video.js/dist/video-js.css';
import 'videojs-contrib-hls';
import './App.css';
import InputPage from './InputPage';
import VideoPlayer from './video.js';

function App() {
  const videoJsOptions = {
    // ... (keep the existing options)
  };

  return (
    <Router>
      <div>
        <Navbar bg="dark" variant="dark">
          <Container>
            <Navbar.Brand>RTMP Player</Navbar.Brand>
            <Dropdown className="ml-auto custom-dropdown">
              <Dropdown.Toggle variant="outline-primary" id="dropdown-menu">
                <span className="navbar-toggler-icon"></span>
              </Dropdown.Toggle>
              <Dropdown.Menu>
                <Dropdown.Item as={Link} to="/dashboard">Dashboard</Dropdown.Item>
                <Dropdown.Item as={Link} to="/input">Input</Dropdown.Item>
                <Dropdown.Item as={Link} to="/sources">Sources</Dropdown.Item>
                <Dropdown.Item as={Link} to="/events">Events</Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
          </Container>
        </Navbar>
        <Switch>
          <Route path="/input">
            <InputPage />
          </Route>
          <Route path="/">
            <div className="app-container">
              <div className="player-wrapper">
                <VideoPlayer {...videoJsOptions} />
              </div>
            </div>
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
