import 'bootstrap/dist/css/bootstrap.min.css';
import React from 'react';
import { Route, BrowserRouter as Router, Routes, useLocation, useMatch } from 'react-router-dom';
import 'video.js/dist/video-js.css';
import 'videojs-contrib-hls';
import './App.css';
import VideoPlayer from './video.js';

function App() {
  const videoJsOptions = {
  autoplay: true,
  controls: true,
  width: '100%', 
  height: '100%',
  sources: [
    {
      src: 'http://localhost:8001/live/test/index.m3u8',
      type: 'application/x-mpegURL',
    },
  ],
};

  return (
    <Router>
      <AppContent videoJsOptions={videoJsOptions} />
    </Router>
  );
}

function AppContent({ videoJsOptions }) {
  const location = useLocation();
  const pageTitle = location.pathname.substring(1);
  const match = useMatch("/input");
  const isInputPage = match !== null;

  return (
    <div className={isInputPage ? 'input-page' : ''}>
      {/* ... */}
      <Routes>
        <Route path="/input" element={
          <div className="app-container">
            <div className={`player-wrapper ${isInputPage ? 'input-player-wrapper' : ''}`}>
              <VideoPlayer options={videoJsOptions} />
            </div>
          </div>
        }/>
        <Route path="/" element={
          <div className="app-container">
            <div className="player-wrapper">
              <VideoPlayer {...videoJsOptions} />
            </div>
          </div>
        } />
      </Routes>
    </div>
  );
}

export default App;
